import { useState } from "react";
// import "./App.css";

function App() {
  const [city, setCity] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("male");
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResult("Fetching...");

    try {
      const response = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ city, age: parseInt(age), gender }),
      });

      if (!response.ok) throw new Error("Error fetching prediction");

      const data = await response.json();
      setResult(data);
    } catch (err) {
      setResult({ error: err.message });
    }
  };

  return (
    <div className="App">
      <h2>Health Risk Prediction</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
          required
        />
        <select value={gender} onChange={(e) => setGender(e.target.value)}>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>
        <button type="submit">Predict</button>
      </form>

      {result && !result.error && (
        <div className="result">
          <p>HealthImpactScore: {result.health_score}</p>
          <p>Age/Gender Score: {result.age_gender_score}</p>
          <p>Final Score: {result.final_score}</p>
          <p>Risk Label: {result.risk_label}</p>
        </div>
      )}
      {result && result.error && <p>Error: {result.error}</p>}
    </div>
  );
}

export default App;
