import { useState } from "react";
import {
  ChakraProvider,
  Box,
  Heading,
  Input,
  Select,
  Button,
  Text,
  VStack,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";

function App() {
  const [city, setCity] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("male");
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResult({ loading: true });

    try {
      const response = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ city, age: parseInt(age), gender }),
      });

      if (!response.ok) throw new Error("Error fetching prediction");

      const data = await response.json();
      setResult({ loading: false, data });
    } catch (err) {
      setResult({ loading: false, error: err.message });
    }
  };

  return (
    <ChakraProvider>
      <Box maxW="md" mx="auto" mt={10} p={6} borderWidth={1} borderRadius="lg" shadow="md">
        <Heading mb={6} textAlign="center">Health Risk Prediction</Heading>
        <VStack as="form" spacing={4} onSubmit={handleSubmit}>
          <Input
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
          <Input
            type="number"
            placeholder="Age"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            required
          />
          <Select value={gender} onChange={(e) => setGender(e.target.value)}>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </Select>
          <Button type="submit" colorScheme="teal" width="full">
            Predict
          </Button>
        </VStack>

        <Box mt={6}>
          {result?.loading && <Text>Fetching prediction...</Text>}
          {result?.error && (
            <Alert status="error">
              <AlertIcon />
              {result.error}
            </Alert>
          )}
          {result?.data && (
            <Box mt={4} p={4} borderWidth={1} borderRadius="md" bg="gray.50">
              <Text><b>HealthImpactScore:</b> {result.data.health_score}</Text>
              <Text><b>Age/Gender Score:</b> {result.data.age_gender_score}</Text>
              <Text><b>Final Score:</b> {result.data.final_score}</Text>
              <Text><b>Risk Label:</b> {result.data.risk_label}</Text>
            </Box>
          )}
        </Box>
      </Box>
    </ChakraProvider>
  );
}

export default App;
